    int pullup;//引体向上或者曲臂悬垂
    int ldrun;//长跑
    int srun;//蛇形跑
    int situps;//仰卧起坐
};
int find(struct STU student[],int n,struct STU champion[]);
int  main( )
{ 
   struct STU student[N],champion[N];
   int n,i,count=0;
   scanf("%d",&n);
   for(i=0;i<n;i++){
       scanf("%s%d %c%d%d%d%d",student[i].name,&student[i].no,&student[i].sex,&student[i].pullup,&student[i].ldrun,&student[i].srun,&student[i].situps);
