           for(int j=0, k=n-1;j<k;j++,k--)
           {
               int t;
               t=matrix[i][j];
               matrix[i][j]=matrix[i][k];
               matrix[i][k]=t;
           }             
    }
    else
    {
        for(int j=0;j<@@[](1);j++) //垂直翻转，对每一列进行逆置
           for(int i=0,k=m-1;@@[](1);i++,k--)
           {
               int t;
               t= matrix@@[](1);
               matrix@@[](1)=matrix@@[](1);
