void process(struct stu s[],int n,struct stu s1);

int main(){
    int i,n,m;
    struct stu s[N],s1;
    n=input_data(s);
    scanf("%d",&m);
    for(i=0;i<m;i++){
       scanf("%s%d%d%d%d%d",s1.name, &s1.no, &s1.lift, &s1.s_run, &s1.km_3, &s1.updn);
       process(s,n,s1);
    }
    return 0;
}

